import React, { useState } from "react";
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";

const Carousel = () => {
  const [current, setCurrent] = useState(0);

  const settings = {
    infinite: true,
    speed: 600,
    slidesToShow: 1,
    slidesToScroll: 1,
    autoplay: true,
    autoplaySpeed: 4000,
    arrows: true,
    dots: false,
    pauseOnHover: true,
    beforeChange: (oldIndex, newIndex) => setCurrent(newIndex % 4),
    swipeToSlide: true,
  };

  const slides = [
    { color: "bg-red-500", label: "SOBRE MÍ", anchor: "#about" },
    { color: "bg-green-500", label: "PROYECTOS", anchor: "#projects" },
    { color: "bg-blue-500", label: "CONTACTO", anchor: "#contact" },
    { color: "bg-yellow-500", label: "OTRO", anchor: "#other" }, // Aquí la barra amarilla
  ];

  return (
    <div className="w-full overflow-hidden relative">
      <div className="h-80">
        <Slider {...settings}>
          {slides.map((slide, index) => (
            <a key={index} href={slide.anchor}>
              <div
                className={`h-80 ${slide.color} flex items-center justify-center text-white text-3xl font-bold relative`}
              >
                {slide.label}
              </div>
            </a>
          ))}
        </Slider>
      </div>

      {/* Indicadores */}
      <div className="absolute bottom-4 left-1/2 transform -translate-x-1/2 flex gap-2">
        {slides.map((_, i) => (
          <div
            key={i}
            className={`h-1 rounded-full transition-all duration-300 cursor-pointer ${
              current === i ? "w-12 bg-yellow-500" : "w-4 bg-white/50"
            }`}
            onClick={() => setCurrent(i)}  // Al hacer click cambia el slide
          />
        ))}
      </div>
    </div>
  );
};

export default Carousel;
