const projects = [
  {
    title: "WauMatch",
    description: "Proyecto que une personas para el cuidado de animales",
    link: "https://tulink.vercel.app",
  },
  {
    title: "Proyecto TFGPoke",
    description: "Pequeño juego Pokemon hecho en SpringBoot utilizando Java , JavaScript , Css y MySQL",
    link: "https://github.com/tuusuario/proyecto-python",
  },
  // Puedes seguir añadiendo más proyectos aquí
];

export default function Projects() {
  return (
    <section id="projects" className="my-12 bg-black text-white py-12">
      <h2 className="text-3xl font-bold text-center mb-8">Proyectos</h2>

      <div className="grid grid-cols-1 md:grid-cols-2 gap-8 max-w-5xl mx-auto px-4">
        {projects.map((project, index) => (
          <div
            key={index}
            className="relative p-6 bg-black border border-white/10 rounded-xl shadow-lg text-white overflow-hidden group"
          >
            {/* Neon animated border */}
            <div className="absolute inset-0 rounded-xl border-2 border-transparent group-hover:border-blue-400 group-hover:animate-neon z-0 before:content-[''] before:absolute before:inset-0 before:rounded-xl before:border-2 before:border-blue-500 before:opacity-0 group-hover:before:opacity-100 group-hover:before:animate-neon-spin" />

            <div className="relative z-10">
              <h3 className="text-xl font-semibold mb-2">{project.title}</h3>
              <p className="mb-3">{project.description}</p>
              <a
                href={project.link}
                target="_blank"
                className="text-blue-400 underline hover:text-blue-300 transition"
              >
                Ver más
              </a>
            </div>
          </div>
        ))}
      </div>
    </section>
  );
}
