export default function Technologies() {
  const sections = [
    {
      title: "💻 Lenguajes de programación",
      items: [
        "java", "csharp", "kotlin", "html5", "css3", "javascript", "php"
      ]
    },
    {
      title: "⚙️ Frameworks y entornos",
      items: [
        "spring", "dotnet", "blazor", "angular", "bootstrap", "xampp"
      ]
    },
    {
      title: "🛢️ Bases de datos",
      items: [
        "mysql", "microsoftsqlserver", "mongodb"
      ]
    },
    {
      title: "🧰 Herramientas",
      items: [
        "git", "github", "docker", "firebase", "intellij", "visualstudio", "vscode", "androidstudio"
      ]
    }
  ];

  const getIconClass = (name) => {
    const mapping = {
      java: "devicon-java-plain",
      csharp: "devicon-csharp-line",
      kotlin: "devicon-kotlin-plain",
      html5: "devicon-html5-plain",
      css3: "devicon-css3-plain",
      javascript: "devicon-javascript-plain",
      php: "devicon-php-plain",
      spring: "devicon-spring-plain",
      dotnet: "devicon-dotnetcore-plain",
      blazor: "devicon-dotnetcore-plain", // no icon, fallback
      angular: "devicon-angularjs-plain",
      bootstrap: "devicon-bootstrap-plain",
      xampp: "devicon-xampp-plain", // Not available, fallback
      mysql: "devicon-mysql-plain",
      microsoftsqlserver: "devicon-microsoftsqlserver-plain", // Not in Devicon, use placeholder
      mongodb: "devicon-mongodb-plain",
      git: "devicon-git-plain",
      github: "devicon-github-original",
      docker: "devicon-docker-plain",
      firebase: "devicon-firebase-plain",
      intellij: "devicon-intellij-plain",
      visualstudio: "devicon-visualstudio-plain",
      vscode: "devicon-vscode-plain",
      androidstudio: "devicon-androidstudio-plain"
    };

    return mapping[name] || "devicon-devicon-plain";
  };

  return (
    <div className="grid grid-cols-1 md:grid-cols-2 gap-6 p-6 bg-[#0d1117] text-white">
      {sections.map((section) => (
        <div key={section.title} className="bg-[#161b22] p-5 rounded-2xl shadow-lg">
          <h2 className="text-xl font-semibold mb-4">{section.title}</h2>
          <div className="flex flex-wrap gap-4">
            {section.items.map((tech) => (
              <div key={tech} className="flex flex-col items-center w-[70px]">
                <i className={`${getIconClass(tech)} colored text-4xl`}></i>
                <span className="text-xs text-center mt-1">{tech}</span>
              </div>
            ))}
          </div>
        </div>
      ))}
    </div>
  );
}
