import React, { useState } from "react";
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";

const Carousel = () => {
  const [current, setCurrent] = useState(0);

  const settings = {
    infinite: true,
    speed: 600,
    slidesToShow: 1,
    slidesToScroll: 1,
    autoplay: true,
    autoplaySpeed: 4000,
    arrows: false,
    dots: false,
    pauseOnHover: true,
    beforeChange: (_, next) => setCurrent(next % 4),
    swipeToSlide: true,
  };

  const slides = [
    { color: "bg-red-500", label: "SOBRE MÍ", anchor: "#about" },
    { color: "bg-green-500", label: "PROYECTOS", anchor: "#projects" },
    { color: "bg-blue-500", label: "CONTACTO", anchor: "#contact" },
    { color: "bg-neutral-800", label: "OTRO", anchor: "#other" }, // cuarto slide
  ];

  return (
    <div className="relative w-full h-80 overflow-hidden">
      <Slider {...settings}>
        {slides.map((slide, index) => (
          <a key={index} href={slide.anchor}>
            <div
              className={`h-80 ${slide.color} flex items-center justify-center text-white text-3xl font-bold relative`}
            >
              {slide.label}

              {/* Barritas dentro del slide */}
              <div className="absolute bottom-4 left-1/2 transform -translate-x-1/2 flex gap-2">
                {slides.map((_, i) => (
                  <div
                    key={i}
                    className={`h-1 rounded-full transition-all duration-300 ${
                      current === i ? "w-12 bg-white" : "w-4 bg-white/50"
                    }`}
                  />
                ))}
              </div>
            </div>
          </a>
        ))}
      </Slider>
    </div>
  );
};

export default Carousel;
