import React from "react";
import Slider from "react-slick";
import { FaArrowLeft, FaArrowRight } from "react-icons/fa";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";

const HeroCarousel = () => {
  const settings = {
    infinite: true,
    speed: 700,
    slidesToShow: 1,
    slidesToScroll: 1,
    arrows: true,
    dots: false,
    autoplay: true,
    autoplaySpeed: 4000,
    pauseOnHover: false,
    prevArrow: <CustomPrevArrow />,
    nextArrow: <CustomNextArrow />,
  };

  return (
    <div className="relative w-full h-screen overflow-hidden">
      <Slider {...settings}>
        <div className="h-screen bg-red-500 flex items-center justify-center">
          <h1 className="text-white text-4xl font-bold">Slide 1</h1>
        </div>
        <div className="h-screen bg-green-500 flex items-center justify-center">
          <h1 className="text-white text-4xl font-bold">Slide 2</h1>
        </div>
        <div className="h-screen bg-blue-500 flex items-center justify-center">
          <h1 className="text-white text-4xl font-bold">Slide 3</h1>
        </div>
      </Slider>
    </div>
  );
};

const CustomPrevArrow = ({ onClick }) => (
  <button
    className="absolute left-4 top-1/2 transform -translate-y-1/2 z-10 text-white text-3xl"
    onClick={onClick}
    aria-label="Previous"
  >
    <FaArrowLeft />
  </button>
);

const CustomNextArrow = ({ onClick }) => (
  <button
    className="absolute right-4 top-1/2 transform -translate-y-1/2 z-10 text-white text-3xl"
    onClick={onClick}
    aria-label="Next"
  >
    <FaArrowRight />
  </button>
);

export default HeroCarousel;
