import React from "react";

const projects = [
  {
    title: "WauMatch",
    description: "Proyecto que une personas para el cuidado de animales",
    link: "https://tulink.vercel.app",
  },
  {
    title: "Proyecto TFGPoke",
    description: "Pequeño juego Pokemon hecho en SpringBoot utilizando Java, JavaScript, Css y MySQL",
    link: "https://github.com/tuusuario/proyecto-python",
  },
  {
    title: "Proyecto Ejemplo 3",
    description: "Descripción de un tercer proyecto de ejemplo",
    link: "https://example.com",
  },
];

export default function Projects() {
  return (
    <section id="projects" className="my-12 bg-[#0d1117] text-white py-12">
      <h2 className="text-3xl font-bold text-center mb-8">Proyectos Personales</h2>

      <div className="max-w-6xl mx-auto px-4">
        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
          {projects.map((project, index) => (
            <div
              key={index}
              className="relative p-6 bg-[#161b22] rounded-lg text-white overflow-hidden group h-[200px] max-w-[200px]"
            >
              {/* Borde estático */}
              <div className="absolute inset-0 rounded-lg border border-gray-700" />
              <div className="border-animator" />

              <div className="relative z-10 flex flex-col justify-between h-full">
                <div>
                  <h3 className="text-lg font-semibold mb-2">{project.title}</h3>
                  <p className="text-sm mb-2 line-clamp-3">{project.description}</p>
                </div>
                <a
                  href={project.link}
                  target="_blank"
                  className="text-pink-400 underline hover:text-pink-300 transition text-sm"
                >
                  Ver más
                </a>
              </div>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
}
