import React from "react";
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";

const Carousel = () => {
  const settings = {
    infinite: true,
    speed: 600,
    slidesToShow: 1,
    slidesToScroll: 1,
    autoplay: true,
    autoplaySpeed: 4000,
    arrows: true,
    dots: false,
    pauseOnHover: true,
    prevArrow: <Arrow direction="left" />,
    nextArrow: <Arrow direction="right" />,
  };

  return (
    <div className="relative w-full h-80 overflow-hidden">
      <Slider {...settings}>
        <a href="#about">
          <div className="h-80 bg-red-500 flex items-center justify-center text-white text-3xl font-bold">
            SOBRE M√ç
          </div>
        </a>
        <a href="#projects">
          <div className="h-80 bg-green-500 flex items-center justify-center text-white text-3xl font-bold">
            PROYECTOS
          </div>
        </a>
        <a href="#contact">
          <div className="h-80 bg-blue-500 flex items-center justify-center text-white text-3xl font-bold">
            CONTACTO
          </div>
        </a>
      </Slider>
    </div>
  );
};

const Arrow = ({ direction, onClick }) => {
  return (
    <button
      onClick={onClick}
      className={`absolute top-1/2 transform -translate-y-1/2 text-white text-4xl font-bold z-10 bg-black/40 px-4 py-2 rounded-full hover:bg-black/60 transition-all ${
        direction === "left" ? "left-4" : "right-4"
      }`}
    >
      {direction === "left" ? "<" : ">"}
    </button>
  );
};

export default Carousel;
