import React from "react";

// Datos de la experiencia
const experiences = [
  {
    title: "Desarrollador Front-End en WauTech",
    description: "Trabajo en el desarrollo de interfaces de usuario utilizando React, Tailwind CSS y otras tecnologías modernas.",
    date: "Enero 2023 - Actualidad",
    link: "https://wauTech.com",
  },
  {
    title: "Desarrollador Full Stack en DevStudio",
    description: "Desarrollé aplicaciones web con tecnologías como Node.js, Express, MongoDB y React.",
    date: "Junio 2021 - Diciembre 2022",
    link: "https://devstudio.com",
  },
  // Puedes agregar más experiencias aquí
];

export default function Experience() {
  return (
    <section id="experience" className="my-12 bg-black text-white py-12">
      <h2 className="text-3xl font-bold text-center mb-8">Experiencia</h2>

      <div className="grid grid-cols-1 md:grid-cols-2 gap-8 max-w-5xl mx-auto px-4">
        {experiences.map((experience, index) => (
          <div
            key={index}
            className="relative p-6 bg-neutral-900 rounded-xl text-white overflow-hidden group"
          >
            {/* Borde estático */}
            <div className="absolute inset-0 rounded-xl border border-gray-700" />
            <div className="border-animator" />

            <div className="relative z-10">
              <h3 className="text-xl font-semibold mb-2">{experience.title}</h3>
              <p className="mb-3">{experience.description}</p>
              <p className="text-sm text-gray-400">{experience.date}</p>
              <a
                href={experience.link}
                target="_blank"
                rel="noopener noreferrer"
                className="text-pink-400 underline hover:text-pink-300 transition"
              >
                Ver más
              </a>
            </div>
          </div>
        ))}
      </div>
    </section>
  );
}
