import { Mail, Github, Linkedin } from "lucide-react";
import { useState } from "react";

export default function Contact() {
  const [copied, setCopied] = useState(false);
  const email = "alejandro.ibanez.samalea@gmail.com";

  const copyEmail = () => {
    navigator.clipboard.writeText(email);
    setCopied(true);
    setTimeout(() => setCopied(false), 2000);
  };

  return (
    <section id="contact" className="my-12 text-center">
      <h2 className="text-2xl font-bold mb-4 text-pink-400">Contacto</h2>
      <div className="flex justify-center gap-6 text-white text-2xl">
        {/* LinkedIn */}
        <a
          href="https://www.linkedin.com/in/tu-usuario"
          target="_blank"
          rel="noopener noreferrer"
          className="hover:text-pink-400 transition"
        >
          <Linkedin />
        </a>

        {/* GitHub */}
        <a
          href="https://github.com/alejandrosamalea"
          target="_blank"
          rel="noopener noreferrer"
          className="hover:text-pink-400 transition"
        >
          <Github />
        </a>

        {/* Gmail */}
        <button
          onClick={copyEmail}
          className="hover:text-pink-400 transition relative"
          title="Copiar correo"
        >
          <Mail />
          {copied && (
            <span className="absolute -top-6 left-1/2 -translate-x-1/2 text-xs bg-gray-800 text-white px-2 py-1 rounded shadow">
              Copiado!
            </span>
          )}
        </button>
      </div>
    </section>
  );
}
