import React from "react";
import Slider from "react-slick";
import { FaArrowLeft, FaArrowRight } from "react-icons/fa";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";

const Carousel = () => {
  const settings = {
    infinite: true,
    speed: 600,
    slidesToShow: 1,
    slidesToScroll: 1,
    autoplay: true,
    autoplaySpeed: 4000,
    arrows: true,
    dots: false,
    pauseOnHover: true,
    prevArrow: <ArrowLeft />,
    nextArrow: <ArrowRight />,
  };

  return (
    <div className="relative w-full max-w-7xl h-72 mx-auto mt-10 px-4 overflow-hidden">
      <Slider {...settings}>
        <a href="#about">
          <div className="h-72 bg-red-500 flex items-center justify-center text-white text-2xl font-bold">
            SOBRE M√ç
          </div>
        </a>
        <a href="#projects">
          <div className="h-72 bg-green-500 flex items-center justify-center text-white text-2xl font-bold">
            PROYECTOS
          </div>
        </a>
        <a href="#contact">
          <div className="h-72 bg-blue-500 flex items-center justify-center text-white text-2xl font-bold">
            CONTACTO
          </div>
        </a>
      </Slider>
    </div>
  );
};

// Componente para la flecha izquierda
const ArrowLeft = ({ onClick }) => (
  <button
    onClick={onClick}
    className="absolute left-4 top-1/2 transform -translate-y-1/2 bg-black/50 text-white p-3 rounded-full z-10"
  >
    <FaArrowLeft />
  </button>
);

// Componente para la flecha derecha
const ArrowRight = ({ onClick }) => (
  <button
    onClick={onClick}
    className="absolute right-4 top-1/2 transform -translate-y-1/2 bg-black/50 text-white p-3 rounded-full z-10"
  >
    <FaArrowRight />
  </button>
);

export default Carousel;
