import React from "react";

const Technologies = () => {
  const sections = [
    {
      title: "üñ•Ô∏è Frontend",
      items: ["html5", "css3", "javascript", "angular", "bootstrap", "vscode"],
    },
    {
      title: "üõ†Ô∏è Backend",
      items: ["java", "csharp", "kotlin", "php", "spring", "dotnet", "blazor"],
    },
    {
      title: "üìö Aprendiendo",
      items: ["mongodb", "mysql", "microsoftsqlserver", "xampp"],
    },
    {
      title: "üß∞ Herramientas",
      items: ["git", "github", "docker", "firebase", "intellij", "visualstudio", "androidstudio"],
    },
  ];

  const getIconClass = (name) => {
    const mapping = {
      html5: "devicon-html5-plain",
      css3: "devicon-css3-plain",
      javascript: "devicon-javascript-plain",
      angular: "devicon-angularjs-plain",
      bootstrap: "devicon-bootstrap-plain",
      java: "devicon-java-plain",
      csharp: "devicon-csharp-line",
      kotlin: "devicon-kotlin-plain",
      php: "devicon-php-plain",
      spring: "devicon-spring-plain",
      dotnet: "devicon-dotnetcore-plain",
      blazor: "devicon-dotnetcore-plain",
      mongodb: "devicon-mongodb-plain",
      mysql: "devicon-mysql-plain",
      microsoftsqlserver: "devicon-microsoftsqlserver-plain",
      xampp: "devicon-xampp-plain",
      git: "devicon-git-plain",
      github: "devicon-github-original",
      docker: "devicon-docker-plain",
      firebase: "devicon-firebase-plain",
      intellij: "devicon-intellij-plain",
      visualstudio: "devicon-visualstudio-plain",
      vscode: "devicon-vscode-plain",
      androidstudio: "devicon-androidstudio-plain",
    };
    return mapping[name] || "devicon-devicon-plain";
  };

  const getIconStyle = (name) => {
    if (name === "github") return { color: "white" };
    return {};
  };

  return (
    <section id="technologies" className="my-12 bg-[#0d1117] text-white py-12">
      <h2 className="text-3xl font-bold text-center mb-8">Tecnolog√≠as</h2>

      <div className="max-w-6xl mx-auto px-4">
        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-6">
          {sections.map((section) => (
            <div
              key={section.title}
              className="relative p-6 bg-[#161b22] rounded-lg text-white overflow-hidden group h-[200px] max-w-[200px]"
            >
              <div className="absolute inset-0 rounded-lg border border-gray-700" />
              <div className="border-animator" />

              <div className="relative z-10 flex flex-col justify-between h-full">
                <div>
                  <h3 className="text-lg font-semibold mb-2">{section.title}</h3>
                  <div className="flex flex-wrap gap-2">
                    {section.items.map((tech) => (
                      <div
                        key={tech}
                        className="w-[40px] h-[40px] bg-gray-800 rounded flex items-center justify-center"
                      >
                        <i
                          className={`${getIconClass(tech)} text-xl colored`}
                          style={getIconStyle(tech)}
                        ></i>
                      </div>
                    ))}
                  </div>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default Technologies;
