import React, { useState } from "react";
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";

const Carousel = () => {
  const [current, setCurrent] = useState(0);

  const settings = {
    infinite: true,
    speed: 600,
    slidesToShow: 1,
    slidesToScroll: 1,
    autoplay: true,
    autoplaySpeed: 4000,
    arrows: true,
    dots: false,
    pauseOnHover: true,
    beforeChange: (oldIndex, newIndex) => setCurrent(newIndex),
    swipeToSlide: true,
  };

  const changeSlide = (index) => {
    setCurrent(index);
  };

  return (
    <div className="w-full overflow-hidden relative">
      <div className="h-80">
        <Slider {...settings}>
          <div className="h-80 bg-red-500 flex items-center justify-center text-white text-3xl font-bold">
            SOBRE MÍ
          </div>
          <div className="h-80 bg-green-500 flex items-center justify-center text-white text-3xl font-bold">
            PROYECTOS
          </div>
          <div className="h-80 bg-blue-500 flex items-center justify-center text-white text-3xl font-bold">
            CONTACTO
          </div>
          <div className="h-80 bg-yellow-500 flex items-center justify-center text-white text-3xl font-bold">
            OTRO
          </div> {/* Slide amarillo */}
        </Slider>
      </div>

      {/* Flechas de navegación */}
      <div className="absolute left-0 top-1/2 transform -translate-y-1/2 z-10 text-4xl text-white cursor-pointer">
        <button
          onClick={() => {
            setCurrent(current === 0 ? 3 : current - 1);
          }}
          className="p-2"
        >
          &lt;
        </button>
      </div>
      <div className="absolute right-0 top-1/2 transform -translate-y-1/2 z-10 text-4xl text-white cursor-pointer">
        <button
          onClick={() => {
            setCurrent(current === 3 ? 0 : current + 1);
          }}
          className="p-2"
        >
          &gt;
        </button>
      </div>

      {/* Indicadores de navegación */}
      <div className="absolute bottom-4 left-1/2 transform -translate-x-1/2 flex gap-2">
        {[0, 1, 2, 3].map((i) => (
          <div
            key={i}
            className={`h-1 w-12 rounded-full transition-all duration-300 cursor-pointer ${
              current === i ? "w-12 bg-black" : "w-4 bg-gray-300"
            }`}
            onClick={() => changeSlide(i)} // Cambiar al slide correspondiente al hacer clic en la barra
          />
        ))}
      </div>
    </div>
  );
};

export default Carousel;
