import React, { useState } from "react";
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";

const Carousel = () => {
  const [current, setCurrent] = useState(0);

  const settings = {
    infinite: true,
    speed: 600,
    slidesToShow: 1,
    slidesToScroll: 1,
    autoplay: true,
    autoplaySpeed: 4000,
    arrows: true,
    dots: false,
    pauseOnHover: true,
    swipeToSlide: true,
    beforeChange: (_, next) => setCurrent(next % slides.length),
    prevArrow: <CustomArrow direction="left" />,
    nextArrow: <CustomArrow direction="right" />,
  };

  const slides = [
    { color: "bg-red-500", label: "SOBRE MÍ", anchor: "#about" },
    { color: "bg-green-500", label: "PROYECTOS", anchor: "#projects" },
    { color: "bg-blue-500", label: "CONTACTO", anchor: "#contact" },
    { color: "bg-neutral-800", label: "OTRO", anchor: "#other" },
  ];

  return (
    <div className="relative w-full h-80 overflow-hidden">
      <Slider {...settings}>
        {slides.map((slide, index) => (
          <a key={index} href={slide.anchor}>
            <div
              className={`h-80 ${slide.color} flex items-center justify-center text-white text-3xl font-bold relative`}
            >
              {slide.label}

              {/* Indicadores visuales */}
              <div className="absolute bottom-4 left-1/2 transform -translate-x-1/2 flex gap-2">
                {slides.map((_, i) => (
                  <div
                    key={i}
                    className={`h-1 rounded-full transition-all duration-300 ${
                      current === i ? "w-12 bg-white" : "w-4 bg-white/50"
                    }`}
                  />
                ))}
              </div>
            </div>
          </a>
        ))}
      </Slider>
    </div>
  );
};

// Flechas personalizadas
const CustomArrow = ({ direction, onClick }) => {
  return (
    <button
      onClick={onClick}
      className={`absolute top-1/2 -translate-y-1/2 z-10 text-white text-4xl font-bold px-3 py-1 bg-black/40 hover:bg-black/70 rounded-full ${
        direction === "left" ? "left-4" : "right-4"
      }`}
    >
      {direction === "left" ? "<" : ">"}
    </button>
  );
};

export default Carousel;
